mapper.select:
		SELECT COUNT(*)
		FROM schedule
		WHERE EXISTS (
    		SELECT 1
    		FROM schedule
    		WHERE year = #{year}
        	AND month = #{month}
        	AND day = #{day}
        	AND (start BETWEEN #{start} AND #{end} OR end BETWEEN #{start} AND #{end})
        	AND is_delete = 'N'
		)

mapper.insert:
    INSERT INTO schedule (
	    start
	    , end
	    , people_num
	    , name
	    , department
	    , contents
	    , is_delete
	    , year
	    , month
	    , day
		)VALUES(
		#{start}
	    , #{end}
	    , #{peopleNum}
	    , #{name}
	    , #{department}
	    , #{contents}
	    , 'N'
	    , #{year}
	    , #{month}
	    , #{day}
	)

mapper.update:
UPDATE schedule SET
is_delete = 'Y'

, cancle_reason = #{cancelReason}


, cancle_name = #{cancelNm}

WHERE id = #{id}
